import React , {useEffect , useState , Fragment}from "react";
import axios from "axios";
import Collection from "./components/collection";
import Contact from "./components/contacts";
import Home from "./components/home";
import Navbar from "./components/navbar";
import News from "./components/news";
import Sellers from "./components/sellers";
import Toptext from "./components/top-txt";
import './index.scss';

const Ecommerce = (props) => { 
    const [itemList , setItemList ] = useState([]);
    const getItem = async () => {
        try{
          const {data} = await axios.get('http://localhost:3001/shopping');
          console.log(data)
          setItemList(data)
        }catch (error){

          setItemList([])
        }
      }

      useEffect(() =>{
        getItem();
      },[])

    return (
        <Fragment>
           <Toptext/>
           <Home/>
           <Sellers itemList={itemList}/>
           <Collection/>
           <Contact/>
           <Navbar/>
           <News/>
            <section id="contact">
            <div className="contact container">
            <div className="map">
            </div>
            <form action="https://formspree.io/f/xzbowpjq" method="POST">
                <div className="form">
                    <div className="form-txt">
                        <h4>INFORMATION</h4>
                        <h1>Contact Us</h1>
                        <span>As you might expect of a company that began as a high-end interiors contractor, we pay strict
                            attention.</span>
                        <h3>USA</h3>
                        <p>195 E Parker Square Dr, Parker, CO 801<br/>+43 982-314-0958</p>
                        <h3>India</h3>
                        <p>HW95+C9C, Lorem ipsum dolor sit.<br/>411014</p>
                    </div>
                    <div className="form-details">
                        <input type="text" name="name" id="name" placeholder="Name" required/>
                        <input type="email" name="email" id="email" placeholder="Email" required/>
                        <textarea name="message" id="message" cols="52" rows="7" placeholder="Message" required></textarea>
                        <button>SEND MESSAGE</button>
                    </div>
                </div>
            </form>
            </div>
            </section>
        </Fragment>
    );
}

export default Ecommerce;
